import React , { Component } from 'react'; 
import { Text } from 'native-base';
import { View, StyleSheet, Image } from 'react-native';
import * as firebase from 'firebase';



const defaultImageUrl = 'data:image/jpeg;base64,'; 

export default class UsersAttendingList extends Component {

    constructor(props) {
        super(props); 

        this.state = {
            userId: '',
        }; 
    }

    async componentWillMount() {
        console.log('conponentWillMount()'); 
    }

    async componentDidMount() {

        console.log('conponentDidMount()'); 

        // Validate the user id
        const userId = this.props.userId; 

        // TODO: - Validate the userId
        if (userId === '') {
            // Handle error here
        }

        // Make the call to get the user name and image url


        var query = firebase.database().ref('/Users/' + userId);
        query.once('value').then((snapshot) => {
            let newFirstName = (snapshot.val() && snapshot.val().firstName) || 'Anonymous'; 
            let newLastName = (snapshot.val() && snapshot.val().lastName) || 'Anonymous'; 

            this.setState({firstName: newFirstName, lastName: newLastName}); 
        }); 
    }

    async componentDidUpdate() {
        console.log('conponentDidUpdate()'); 
    }

    render() {
        return (
            <View style={styles.userContainer}>
                <View style={styles.userImageContainer}>
                    <Image style={styles.userImage} resizeMode={'contain'} source={{uri: ''}} defaultSource={{uri: defaultImageUrl}}/>
                </View>
                <Text style={styles.firstName} numberOfLines={1}>{this.state.firstName}</Text>
                <Text style={styles.lastName} numberOfLines={1}>{this.state.lastName}</Text>
            </View>
        )
    }
}

const styles = StyleSheet.create({
    userContainer: {
        flexDirection: 'column', 
        justifyContent: 'center',
        alignItems: 'center',
        width: 80,
        marginHorizontal: 20, 
    },
    userImageContainer: {
        flex: 3,
        alignItems: 'center', 
        justifyContent: 'center',
    },
    userImage: {
        height: 80,
        width: 80,
        borderRadius: 80/2,
    }, 
    firstName: {
        flex: 1,
        fontSize: 16, 
        fontWeight: '400',
        alignSelf: 'center',
    }, 
    lastName: {
        flex: 1,
        fontSize: 16, 
        fontWeight: '400',
        alignSelf: 'center',
    },
 });